  console.log(data)

  const filterData = data.filter((item) => {
    if (item.itemTags.length == 0) {
      return item
    } else if (item.itemTags.toLowerCase().include('love')) {
      return item
    }
  })

  console.log(filterData)

  return (
    <>
      {filterData.map((item, index) => (
        <div key={item.id}>
          <SearchResultItem
            item={item}
            setTagList={setTagList}
            tagList={tagList}
            data={data}
            index={index}
          />
        </div>
      ))}
    </>
  )
}

export default SearchResult
