  //       item.biography.fullName.toLowerCase().includes(searchText.toLowerCase())
  //     ) {
  //       return item
  //     }
  //   })
  // }

  return (
    <>
      {filterData.map((item, index) => (
        <div key={item.id}>
          <SearchResultItem
            item={item}
            setTagList={setTagList}
            tagList={tagList}
            data={data}
            index={index}
          />
        </div>
      ))}
    </>
  )
}

export default SearchResult
